import { loadFromGitHub } from 'gowm';

// Load data processing module from wasm-modules-repository
const jsonxml = await loadFromGitHub('benoitpetit/wasm-modules-repository', {
  path: 'jsonxml-wasm',
  filename: 'main.wasm',
  name: 'jsonxml-wasm',
  branch: 'master'
});

// Configure module
jsonxml.call('setSilentMode', true);

// JSON parsing with error handling
const jsonData = '{"user":{"name":"John","age":30,"active":true}}';
const parseResult = jsonxml.call('parseJSON', jsonData);
if (parseResult.error) {
  console.error('Parse error:', parseResult.error);
} else {
  console.log('Parsed data:', parseResult.data);
  console.log('Valid JSON:', parseResult.valid);
}

// XML to JSON conversion
const xmlData = `
<users>
  <user id="1">
    <name>John Doe</name>
    <age>30</age>
    <email>john@example.com</email>
  </user>
  <user id="2">
    <name>Jane Smith</name>
    <age>25</age>
    <email>jane@example.com</email>
  </user>
</users>`;

const conversionResult = jsonxml.call('xmlToJSON', xmlData);
if (conversionResult.error) {
  console.error('Conversion failed:', conversionResult.error);
} else {
  console.log('Converted JSON:', conversionResult.data);
}

// JSON to XML conversion
const jsonUsers = JSON.stringify({
  users: {
    user: [
      { id: 1, name: 'John Doe', age: 30 },
      { id: 2, name: 'Jane Smith', age: 25 }
    ]
  }
});

const xmlResult = jsonxml.call('jsonToXML', jsonUsers, 'root');
if (xmlResult.error) {
  console.error('XML conversion failed:', xmlResult.error);
} else {
  console.log('Generated XML:', xmlResult.data);
}

// CSV to JSON conversion with type detection
const csvData = `name,age,active,salary,joinDate
John Doe,30,true,75000.50,2023-01-15
Jane Smith,25,false,65000.00,2023-03-20
Bob Johnson,35,true,85000.75,2022-11-10`;

const csvResult = jsonxml.call('csvToJSON', csvData);
if (csvResult.error) {
  console.error('CSV conversion failed:', csvResult.error);
} else {
  console.log('Converted data:', csvResult.data);
  console.log('Rows:', csvResult.rows, 'Columns:', csvResult.columns);
}

// JSON array to CSV conversion
const jsonArray = JSON.stringify([
  { product: 'Laptop', price: 999.99, inStock: true },
  { product: 'Mouse', price: 29.99, inStock: false },
  { product: 'Keyboard', price: 79.99, inStock: true }
]);

const csvGenResult = jsonxml.call('jsonToCSV', jsonArray);
if (csvGenResult.error) {
  console.error('CSV generation failed:', csvGenResult.error);
} else {
  console.log('Generated CSV:');
  console.log(csvGenResult.data);
}

// YAML processing examples
const yamlConfig = `
database:
  host: localhost
  port: 5432
  name: myapp
  credentials:
    username: admin
    password: secret
features:
  - authentication
  - logging
  - caching
settings:
