  // Extract specific configuration values
  const dbConfig = jsonxml.call('extractJSONPath', JSON.stringify(yamlResult.data), 'database.credentials.username');
  console.log('Database username:', dbConfig.data);
}

// Convert JSON back to YAML
const configJson = JSON.stringify(yamlResult.data);
const yamlGenResult = jsonxml.call('jsonToYAML', configJson);
if (yamlGenResult.error) {
  console.error('YAML generation failed:', yamlGenResult.error);
} else {
  console.log('Generated YAML:');
  console.log(yamlGenResult.data);
}

// Helper function for consistent data processing error handling
function handleDataResult(result, successCallback, errorCallback) {
  if (result.error) {
    errorCallback(result.error);
  } else {
    successCallback(result);
  }
}

// Data validation pipeline
const dataOperations = [
  { type: 'JSON', data: '{"name":"John","age":30}', operation: 'parseJSON' },
  { type: 'XML', data: '<user><name>Jane</name></user>', operation: 'parseXML' },
  { type: 'CSV', data: 'name,age\nBob,25', operation: 'csvToJSON' },
  { type: 'YAML', data: 'name: Alice\nage: 28', operation: 'yamlToJSON' }
];

dataOperations.forEach(({ type, data, operation }, index) => {
  const result = jsonxml.call(operation, data);
  handleDataResult(
    result,
    (processedData) => {
      console.log(`${type} processing (${index + 1}/${dataOperations.length}): Success`);
      console.log('  Processed data:', processedData.data);
      console.log('  Valid:', processedData.valid);
    },
    (error) => {
      console.error(`${type} processing (${index + 1}/${dataOperations.length}) failed: ${error}`);
    }
  );
});

import React, { useState, useEffect } from 'react';
import { useWasmFromGitHub } from 'gowm/hooks/useWasm';

function DataConverter() {
  const { wasm: jsonxml, loading, error } = useWasmFromGitHub('benoitpetit/wasm-modules-repository', {
    path: 'jsonxml-wasm',
    filename: 'main.wasm',
    name: 'jsonxml-wasm'
  });

  const [inputData, setInputData] = useState('');
  const [outputData, setOutputData] = useState('');
  const [inputFormat, setInputFormat] = useState('json');
  const [outputFormat, setOutputFormat] = useState('xml');
  const [functions, setFunctions] = useState([]);

  useEffect(() => {
    if (jsonxml) {
      jsonxml.call('setSilentMode', true);
      const availableFunctions = jsonxml.call('getAvailableFunctions');
      setFunctions(availableFunctions);
    }
  }, [jsonxml]);

  const convertData = () => {
    if (!jsonxml || !inputData) return;

    let conversionFunction = '';
    if (inputFormat === 'json' && outputFormat === 'xml') {
      conversionFunction = 'jsonToXML';
    } else if (inputFormat === 'xml' && outputFormat === 'json') {
      conversionFunction = 'xmlToJSON';
    } else if (inputFormat === 'csv' && outputFormat === 'json') {
      conversionFunction = 'csvToJSON';
    } else if (inputFormat === 'yaml' && outputFormat === 'json') {
      conversionFunction = 'yamlToJSON';
    }

    if (conversionFunction) {
      const result = jsonxml.call(conversionFunction, inputData);
      if (result.error) {
        setOutputData(`Error: ${result.error}`);
      } else {
        setOutputData(typeof result.data === 'string' ? result.data : JSON.stringify(result.data, null, 2));
      }
    }
  };

  if (loading) return <div>Loading data converter...</div>;
  if (error) return <div>Error: {error.message}</div>;

  return (
    <div className="data-converter">
      <div className="format-selectors">
        <select value={inputFormat} onChange={(e) => setInputFormat(e.target.value)}>
          <option value="json">JSON</option>
          <option value="xml">XML</option>
          <option value="csv">CSV</option>
          <option value="yaml">YAML</option>
        </select>
        <span> â†’ </span>
        <select value={outputFormat} onChange={(e) => setOutputFormat(e.target.value)}>
          <option value="json">JSON</option>
          <option value="xml">XML</option>
          <option value="csv">CSV</option>
          <option value="yaml">YAML</option>
        </select>
      </div>

      <div className="conversion-area">
        <textarea
          value={inputData}
          onChange={(e) => setInputData(e.target.value)}
          placeholder={`Enter ${inputFormat.toUpperCase()} data...`}
          rows={10}
          cols={50}
        />
        <button onClick={convertData}>Convert</button>
        <textarea
          value={outputData}
          readOnly
          placeholder={`${outputFormat.toUpperCase()} output will appear here...`}
          rows={10}
          cols={50}
        />
      </div>

      <div className="module-info">
        <p>Available functions: {functions.length}</p>
      </div>
    </div>
  );
}

