import { loadFromGitHub } from 'gowm';

// Load PDF module from wasm-modules-repository
const pdf = await loadFromGitHub('benoitpetit/wasm-modules-repository', {
  path: 'pdf-wasm',
  filename: 'main.wasm',
  name: 'pdf-wasm',
  branch: 'master'
});

// Configure module
pdf.call('setSilentMode', true);

// Create PDF with error handling
const pages = JSON.stringify([{
  content: 'Hello PDF World!\nThis is my first page.',
  margin: 15
}]);

const result = pdf.call('createPDF', pages);
if (result.error) {
  console.error('PDF creation error:', result.error);
} else {
  console.log('PDF created successfully!');
  console.log('Size:', result.size, 'bytes');
  console.log('Pages:', result.pages);

  // Download the PDF
  const blob = new Blob([atob(result.pdfData)], {type: 'application/pdf'});
  const url = URL.createObjectURL(blob);
  const a = document.createElement('a');
  a.href = url;
  a.download = 'my-document.pdf';
  a.click();
}

// Complete PDF workflow with multiple operations
async function processPDF() {
  // Step 1: Create initial PDF
  const pages = JSON.stringify([{
    content: 'Original Document\nThis is the main content.',
    margin: 10
  }]);

  let result = pdf.call('createPDF', pages, JSON.stringify({
    title: 'Business Report',
    author: 'John Doe',
    subject: 'Quarterly Analysis'
  }));

  if (result.error) throw new Error(result.error);
  let pdfData = result.pdfData;

  // Step 2: Add watermark
  const watermark = JSON.stringify({
    text: 'CONFIDENTIAL',
    opacity: 0.3,
    rotation: 45
  });

  result = pdf.call('addWatermark', pdfData, watermark);
  if (result.error) throw new Error(result.error);
  pdfData = result.pdfData;

  // Step 3: Compress PDF
  result = pdf.call('compressPDF', pdfData, 'high');
  if (result.error) throw new Error(result.error);

  console.log('Final PDF processed:');
  console.log('- Original size:', result.originalSize, 'bytes');
  console.log('- Compressed size:', result.compressedSize, 'bytes');
  console.log('- Compression ratio:', result.compressionRatio + '%');

  return result.pdfData;
}

import React, { useState, useEffect } from 'react';
import { useWasmFromGitHub } from 'gowm/hooks/useWasm';

function PDFGenerator() {
  const { wasm: pdf, loading, error } = useWasmFromGitHub('benoitpetit/wasm-modules-repository', {
    path: 'pdf-wasm',
    filename: 'main.wasm',
    name: 'pdf-wasm',
    branch: 'master'
  });

  const [content, setContent] = useState('Hello PDF World!');
  const [pdfResult, setPdfResult] = useState(null);
  const [functions, setFunctions] = useState([]);

  useEffect(() => {
    if (pdf) {
      pdf.call('setSilentMode', true);
      const availableFunctions = pdf.call('getAvailableFunctions');
      setFunctions(availableFunctions);
    }
  }, [pdf]);

  const generatePDF = () => {
    if (!pdf) return;

    const pages = JSON.stringify([{
      content: content,
      margin: 15
    }]);

    const result = pdf.call('createPDF', pages);
    if (result.error) {
      setPdfResult({ error: result.error });
    } else {
      setPdfResult(result);

      // Auto-download
      const blob = new Blob([atob(result.pdfData)], {type: 'application/pdf'});
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'generated-document.pdf';
      a.click();
    }
  };

  if (loading) return React.createElement('div', null, 'Loading PDF module...');
  if (error) return React.createElement('div', null, 'Error: ' + error.message);

  return React.createElement('div', null,
    React.createElement('h3', null, 'PDF Functions Available: ' + functions.length),
    React.createElement('textarea', {
      value: content,
      onChange: (e) => setContent(e.target.value),
      placeholder: 'PDF content',
      rows: 5,
      cols: 50
    }),
    React.createElement('br'),
    React.createElement('button', { onClick: generatePDF }, 'Generate PDF'),
    pdfResult && React.createElement('div', { style: { marginTop: '10px' } },
      pdfResult.error
        ? React.createElement('div', { style: { color: 'red' } }, 'Error: ' + pdfResult.error)
        : React.createElement('div', { style: { color: 'green' } },
            'âœ… PDF generated successfully!',
            React.createElement('br'),
            'ðŸ“„ Size: ' + pdfResult.size + ' bytes',
            React.createElement('br'),
            'ðŸ“‹ Pages: ' + pdfResult.pages
          )
    )
  );
}
