import { loadFromGitHub } from 'gowm';

// Load QR module
const qr = await loadFromGitHub('your-org/wasm-projects', {
  path: 'qr-wasm',
  name: 'qr'
});

// Configure module
qr.call('setSilentMode', true);

// Generate QR code with error handling
const result = qr.call('generateQRCode', 'Hello QR World!', 256, 'HIGH');
if (result.error) {
  console.error('QR generation error:', result.error);
} else {
  console.log('QR Base64:', result.base64Image);
  const img = document.createElement('img');
  img.src = 'data:image/png;base64,' + result.base64Image;
  document.body.appendChild(img);
}

const contact = {
  name: 'Alice Smith',
  organization: 'TechCorp Inc.',
  phone: '+1-555-0123',
  email: 'alice@techcorp.com',
  url: 'https://alice.techcorp.com'
};

const result = qr.call('generateVCard', contact, 300);
if (!result.error) {
  console.log('vCard QR generated:', result.base64Image);
}

const wifi = {
  ssid: 'MyOfficeWiFi',
  password: 'SecurePassword123',
  security: 'WPA',
  hidden: false
};

const result = qr.call('generateWiFiQR', wifi, 256);
if (!result.error) {
  console.log('WiFi QR generated for network:', wifi.ssid);
  // Users can scan this QR to auto-connect to WiFi
}

const result = qr.call('generateBarcode', '1234567890128', 'ean13', 400, 200);
if (!result.error) {
  console.log('Barcode type:', result.type);
  console.log('Dimensions:', result.width + 'x' + result.height);
  console.log('Base64 image:', result.base64Image);
}
